document.addEventListener('DOMContentLoaded', function() {

  // 1. Buscamos el contenedor principal de la canción. Si no existe, no hacemos nada más.
  const songContainer = document.querySelector('.cancion-container');
  if (!songContainer) return;

  const chordProContainer = songContainer.querySelector('pre.cancion-chordpro');
  if (!chordProContainer) return;

  // 2. Definimos nuestras herramientas y una variable para la canción.
  const parser = new ChordSheetJS.ChordProParser();
  const formatter = new ChordSheetJS.HtmlDivFormatter();
  let song; // Aquí guardaremos la canción una vez procesada.

  // 3. Creamos una función para "dibujar" o actualizar la canción en la página.
  function renderSong() {
    if (!song) return;
    const keyDisplay = document.getElementById('tonalidad-actual');
    let songOutputDiv = songContainer.querySelector('.cancion-renderizada');

    // Si es la primera vez, creamos el div para la canción.
    if (!songOutputDiv) {
      songOutputDiv = document.createElement('div');
      songOutputDiv.className = 'cancion-renderizada';
      chordProContainer.parentNode.replaceChild(songOutputDiv, chordProContainer);
    }

    // Actualizamos el contenido HTML y la tonalidad visible.
    songOutputDiv.innerHTML = formatter.format(song);
    if (keyDisplay) keyDisplay.textContent = song.key || 'N/A';
  }

  // 4. Procesamos el texto ChordPro por primera vez.
  try {
    const chordProText = chordProContainer.textContent;
    song = parser.parse(chordProText);
    renderSong(); // Llamamos a la función para que se vea la canción.
  } catch (error) {
    console.error('Error al parsear ChordPro:', error);
    chordProContainer.textContent = 'Error: El formato de la canción es incorrecto.';
    return; // Si hay error, nos detenemos.
  }

  // 5. Creamos el "vigilante" de clics para los botones (Delegación de Eventos).
  songContainer.addEventListener('click', function(event) {
    const targetId = event.target.id;

    if (targetId === 'transpose-up') {
      song.transposeUp();
      renderSong();
    }

    if (targetId === 'transpose-down') {
      song.transposeDown();
      renderSong();
    }

    if (targetId === 'descargar-pdf') {
      const elementToCapture = songContainer.querySelector('.cancion-renderizada .chord-sheet');
      const songTitle = song.title || 'Cancion';
      const songArtist = song.subtitle || 'Artista';
      const filename = `${songTitle.replace(/\s/g, '-')}-${songArtist.replace(/\s/g, '-')}.pdf`;

      html2canvas(elementToCapture, { backgroundColor: '#000000' }).then(canvas => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jspdf.jsPDF({ orientation: 'p', unit: 'mm', format: 'a4' });
        const pdfWidth = pdf.internal.pageSize.getWidth();
        const imgProps = pdf.getImageProperties(imgData);
        const imgHeight = (imgProps.height * (pdfWidth - 20)) / imgProps.width;

        pdf.setFontSize(18);
        pdf.text(songTitle, 15, 20);
        pdf.setFontSize(12);
        pdf.text(songArtist, 15, 27);
        pdf.addImage(imgData, 'PNG', 10, 35, pdfWidth - 20, imgHeight);
        pdf.save(filename);
      });
    }
  });
});
//

//
document.addEventListener('DOMContentLoaded', function() {
  const toggleButton = document.querySelector('.toggle-theme');
  const body = document.body;
  const icon = document.getElementById('theme-icon');

  // Verificar si el usuario ya tenía un tema guardado
  if (localStorage.getItem('tema') === 'light') {
    body.classList.add('light-mode');
  }

  toggleButton.addEventListener('click', function() {
    body.classList.toggle('light-mode');

    // Guardar la preferencia del usuario
    if (body.classList.contains('light-mode')) {
      localStorage.setItem('tema', 'light');
    } else {
      localStorage.setItem('tema', 'dark');
    }
  });
});
